function padIntoTwoDigitString(t){return("0"+t).slice(-2)}function replaceTextContent(t,e,n){t.textContent=t.textContent.replace(e,n)}function renderDate(){var t=new Date;let e=t.getFullYear();let n=padIntoTwoDigitString(t.getMonth()+1);let o=padIntoTwoDigitString(t.getDate()+1);let l=document.getElementsByClassName("js-date");for(let t=0;t<l.length;t++){let a=l[t];replaceTextContent(a,/yyyy/g,e);replaceTextContent(a,/mm/g,n);replaceTextContent(a,/dd/g,o)}}