function padIntoTwoDigitString(integer){return integer.toString().padStart(2,"0")}function replaceTextContent(elem,regex,replacement){elem.textContent=elem.textContent.replace(regex,replacement)}function renderDate(){let dateNow=new Date;let yyyy=dateNow.getFullYear();let mm=padIntoTwoDigitString(dateNow.getMonth()+1);let dd=padIntoTwoDigitString(dateNow.getDate());let date_elements=document.getElementsByClassName("js-date");for(let i=0;i<date_elements.length;i++){let elem=date_elements[i];replaceTextContent(elem,/yyyy/g,yyyy);replaceTextContent(elem,/mm/g,mm);replaceTextContent(elem,/dd/g,dd)}}